#' Latent Transition Analysis
#'
#' 
#' @param data .
#' @param factors a list containing named lists that define the \code{label}
#'   of the factor and the \code{vars} that belong to that factor
#' @param covs .
#' @param form1 .
#' @param method .
#' @param cons .
#' @param nc .
#' @param par .
#' @param reg .
#' @param par1 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$post} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$reg} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$member} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$text1} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text2} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text3} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$reg$asDF}
#'
#' \code{as.data.frame(results$reg)}
#'
#' @export
lta <- function(
    data,
    factors = list(
                list(label="L1[2]", vars=list())),
    covs,
    form1 = "L1 ~ SEX+RACE",
    method = "ML",
    cons = "L1,L2, L3",
    nc = 2,
    par = FALSE,
    reg = FALSE,
    par1 = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("lta requires jmvcore to be installed (restart may be required)")

    if ( ! missing(covs)) covs <- jmvcore::resolveQuo(jmvcore::enquo(covs))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(covs), covs, NULL))


    options <- ltaOptions$new(
        factors = factors,
        covs = covs,
        form1 = form1,
        method = method,
        cons = cons,
        nc = nc,
        par = par,
        reg = reg,
        par1 = par1)

    analysis <- ltaClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

