#' Latent Transition Analysis
#'
#' 
#' @param data .
#' @param factors a list containing named lists that define the \code{label}
#'   of the factor and the \code{vars} that belong to that factor
#' @param covs .
#' @param form1 .
#' @param method .
#' @param cons .
#' @param fit .
#' @param est .
#' @param desc .
#' @param cp .
#' @param plot1 .
#' @param plot .
#' @param raw .
#' @param width .
#' @param height .
#' @param width1 .
#' @param height1 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text1} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text2} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text3} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text4} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' @export
lta <- function(
    data,
    factors = list(
                list(label="l1[1]", vars=list())),
    covs,
    form1 = "lc1 ~ SEX",
    method = "naive",
    cons = "cl1,cl2",
    fit = FALSE,
    est = FALSE,
    desc = TRUE,
    cp = FALSE,
    plot1 = FALSE,
    plot = FALSE,
    raw = "FALSE",
    width = 500,
    height = 500,
    width1 = 500,
    height1 = 500) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("lta requires jmvcore to be installed (restart may be required)")

    if ( ! missing(covs)) covs <- jmvcore::resolveQuo(jmvcore::enquo(covs))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(covs), covs, NULL))


    options <- ltaOptions$new(
        factors = factors,
        covs = covs,
        form1 = form1,
        method = method,
        cons = cons,
        fit = fit,
        est = est,
        desc = desc,
        cp = cp,
        plot1 = plot1,
        plot = plot,
        raw = raw,
        width = width,
        height = height,
        width1 = width1,
        height1 = height1)

    analysis <- ltaClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

